function lbgo(e){if(e.indexOf("?")>-1){var t="&SQ_DESIGN_NAME=blank&SQ_LAYOUT_NAME=blank"}else{var t="?SQ_DESIGN_NAME=blank&SQ_LAYOUT_NAME=blank"}$("html, body").animate({scrollTop:0},300);$(".lb-inner").load(e+t,function(){$(".lb-screen").fadeIn(300);bindForms()})}function lbclose(){$(".lb-screen").fadeOut(300,function(){$(".lb-inner").html("")})}function lbchange(e){$(".lb-inner").slideUp(300,function(){$(".lb-inner").html(e).slideDown(300);bindForms()})}function bindForms(){$(".lb-content form").ajaxForm({success:function(e){lbchange(e)}})}$(document).keyup(function(e){e.preventDefault();if(e.keyCode==27){lbclose()}});$(".lb-close").click(function(){lbclose()});$('a[data-lb="true"]').each(function(e,t){$(this).click(function(e){e.preventDefault();lbgo($(this).attr("href"))})});